import { FETCH_PRODUCTS } from "../types";


export const fetchProducts = () => async(dispatch) => {

    //get data from server

    //convert to data to json

    const data = res.json();
    console.log(data);

   const res = await fetch("/api/products");

   dispatch({
       type: FETCH_PRODUCTS,
       payload: data,
   });

};
